import { useState } from 'react';
import Layout from './components/Layout';
import MainPage from './pages/MainPage';
import PlayersPage from './pages/PlayersPage';
import ComparisonsPage from './pages/ComparisonsPage';
import PlayerDetailsPage from './pages/PlayerDetailsPage';
import Header from './components/Header';
import SecondHeader from './components/SecondHeader';
import PlayerCard from './components/PlayerCard';
import DetailedPlayerCard from './components/DetailedPlayerCard';
import TopPlayerItem from './components/TopPlayerItem';
import StatsRadarChart from './components/StatsRadarChart';
import SearchBar from './components/SearchBar';
import { players } from './data/players';

const App = () => {
  const [currentPage, setCurrentPage] = useState('mainPage');
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  const navigateToPage = (page, player = null) => {
    setCurrentPage(page);
    if (player) setSelectedPlayer(player);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'mainPage':
        return <MainPage navigateToPage={navigateToPage} />;
      case 'playersPage':
        return <PlayersPage navigateToPage={navigateToPage} />;
      case 'comparisons':
        return <ComparisonsPage navigateToPage={navigateToPage} />;
      case 'playerDetails':
        return <PlayerDetailsPage player={selectedPlayer} navigateToPage={navigateToPage} />;
      default:
        return <MainPage navigateToPage={navigateToPage} />;
    }
  };

  return (
    <div className="font-sans" dir="rtl">
      <Layout navigateToPage={navigateToPage}>
        {renderPage()}
      </Layout>
    </div>
  );
};

export default App;
