import { useState } from 'react';
import { Grid, List } from 'lucide-react';
import PlayerCard from '../components/PlayerCard';
import { players } from '../data/players';

const MainPage = ({ navigateToPage }) => {
  const [viewMode, setViewMode] = useState('grid');
  const [selectedTeam, setSelectedTeam] = useState('جميع الأندية');
  const [selectedAge, setSelectedAge] = useState('جميع الأعمار');

  const filteredPlayers = players.filter(player => {
    const matchesTeam = selectedTeam === 'جميع الأندية' || player.team === selectedTeam;
    const matchesAge = selectedAge === 'جميع الأعمار' || 
                      (selectedAge === '20-25' && player.age >= 20 && player.age <= 25) ||
                      (selectedAge === '26-30' && player.age >= 26 && player.age <= 30);

    return matchesTeam && matchesAge;
  });

  return (
    <div>
      {/* Title and Filters */}
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center">
          <h1 className="text-xl font-bold ml-4">اللاعبون المميزون</h1>
          <select 
            className="border rounded-md p-2 mx-2"
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
          >
            <option>جميع الأندية</option>
            <option>النصر</option>
            <option>الهلال</option>
            <option>الاتحاد</option>
          </select>
          <select 
            className="border rounded-md p-2 mx-2"
            value={selectedAge}
            onChange={(e) => setSelectedAge(e.target.value)}
          >
            <option>جميع الأعمار</option>
            <option>20-25</option>
            <option>26-30</option>
          </select>
        </div>
        <div className="flex border rounded-md">
          <button 
            className={`p-2 ${viewMode === 'grid' ? 'bg-gray-100' : 'bg-white'}`}
            onClick={() => setViewMode('grid')}
          >
            <Grid size={16} />
          </button>
          <button 
            className={`p-2 ${viewMode === 'list' ? 'bg-gray-100' : 'bg-white'}`}
            onClick={() => setViewMode('list')}
          >
            <List size={16} />
          </button>
        </div>
      </div>

      {/* Players Grid */}
      <div className={viewMode === 'grid' ? 'grid grid-cols-3 gap-6' : 'space-y-4'}>
        {filteredPlayers.map(player => (
          <PlayerCard
            key={player.id}
            player={player}
            onClick={() => navigateToPage('playerDetails', player)}
          />
        ))}
      </div>
    </div>
  );
};

export default MainPage;
