import { useState } from 'react';
import { players } from '../data/players';
import { Grid, List } from 'lucide-react';
import StatsRadarChart from '../components/StatsRadarChart';

const ComparisonsPage = ({ navigateToPage }) => {
  const [selectedPlayers, setSelectedPlayers] = useState([]);
  const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'

  const handlePlayerSelect = (player) => {
    if (selectedPlayers.includes(player)) {
      setSelectedPlayers(selectedPlayers.filter(p => p.id !== player.id));
    } else if (selectedPlayers.length < 2) {
      setSelectedPlayers([...selectedPlayers, player]);
    }
  };

  return (
    <div className="min-h-screen">
      <h1 className="text-2xl font-bold mb-6">صفحة المقارنات</h1>
      <p>محتوى صفحة المقارنات</p>
      <div className="p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">مقارنة اللاعبين</h1>
          <div className="flex space-x-2 space-x-reverse">
            <button 
              className={`p-2 ${viewMode === 'grid' ? 'bg-gray-100' : 'bg-white'}`}
              onClick={() => setViewMode('grid')}
            >
              <Grid size={16} />
            </button>
            <button 
              className={`p-2 ${viewMode === 'list' ? 'bg-gray-100' : 'bg-white'}`}
              onClick={() => setViewMode('list')}
            >
              <List size={16} />
            </button>
          </div>
        </div>

        {selectedPlayers.length > 0 && (
          <div className="grid grid-cols-2 gap-6 mb-6">
            {selectedPlayers.map((player, index) => (
              <div key={index} className="bg-white p-4 rounded-lg shadow">
                <h2 className="text-xl font-bold">{player.name}</h2>
                <div className="mt-4">
                  <p>النادي: {player.team}</p>
                  <p>العمر: {player.age}</p>
                  <p>نسبة النجاح: {player.successRate}%</p>
                  <p>عدد المباريات: {player.matches}</p>
                  <p>التقييم: {player.rating}</p>
                  <p>توزيع ناجح: 88%</p>
                  <p>تصديات/مباراة: 3.8</p>
                  <p>نسبة الإنقاذ: 82%</p>
                </div>
                <StatsRadarChart />
              </div>
            ))}
          </div>
        )}

        <div className={`${viewMode === 'grid' ? 'grid grid-cols-4' : 'flex flex-col'} gap-4`}>
          {players.map((player) => (
            <div
              key={player.id}
              className={`p-4 border rounded-lg cursor-pointer ${
                selectedPlayers.includes(player) ? 'bg-green-100' : 'bg-white'
              }`}
              onClick={() => handlePlayerSelect(player)}
            >
              <h3 className="font-bold">{player.name}</h3>
              <p className="text-sm text-gray-600">{player.team}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ComparisonsPage;
